import './style.css'

//set the dimensions and margins of the graph
let margin = {top: 10, right: 30, bottom: 90, left: 40},
    width = 460 - margin.left - margin.right,
    height = 450 - margin.top - margin.bottom;

//append the svg object to the body of the page
let svg = d3.select("#barras")
  .append("svg")
    .attr("width", width + margin.left + margin.right)
    .attr("height", height + margin.top + margin.bottom)
  .append("g")
    .attr("transform",
          "translate(" + margin.left + "," + margin.top + ")");

//Parse the Data
d3.csv("datasets/pasajeros_2020.csv", function(data) {
console.log("HOLA", data)


  const puertos = data.map(d => d.Puerto);
  const unidades = data.map(d => {
    return parseInt(d.Unidades)});
  // X axis
  let x = d3.scaleBand()
    .range([ 0, width ])
    .domain(puertos)
    .padding(0.2);
  svg.append("g")
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(x))
    .selectAll("text")
      .attr("transform", "translate(-10,0)rotate(-45)")
      .style("text-anchor", "end");
  const maximo = Math.max(...unidades);
  console.log("holi", maximo)
  // Add Y axis
  let y = d3.scaleLinear()
    .domain([0, maximo])
    .range([ height, 0]);
  svg.append("g")
    .call(d3.axisLeft(y));

  // Bars
  svg.selectAll("barras")
    .data(data)
    .enter()
    .append("rect")
      .attr("x", function(d) {return x(d.Puerto); })
      .attr("width", x.bandwidth())
      .attr("fill", "#69b3a2")
      // no bar at the beginning thus:
      .attr("height", function(d) {return height - y(0); }) // always equal to 0
      .attr("y", function(d) { return y(0); })

  //Animation
  svg.selectAll("rect")
    .transition()
    .duration(800)
    .attr("y", function(d) { return y(parseInt(d.Unidades)); })
    .attr("height", function(d) { return height - y(parseInt(d.Unidades)); })

  });
